mapper.update:
		update member 
		set 
			phone = #{phone}
			,email = #{email}
			,nickname = #{nickname}
			,mem_pwd = #{mem_pwd}

		where mem_no = #{mem_no}

mapper.delete:
		
		{call
	        declare
	        begin
	            delete from b_comment where mem_no=#{mem_no};
	            delete from board where mem_no=#{mem_no};
	        	delete from member where mem_no=#{mem_no};
        end}

mapper.select:
select * from( select rownum RNUM,zxcvb.*from( select r.complex_name "complex_name" ,r.rental_regdate "rental_regdate" ,rd.rental_detail_no "rental_detail_no" from rental r, rental_detail rd where 1=1 and r.rental_no = rd.rental_no
and r.loc_no=#{loc_no}
order by 2 desc ) zxcvb where rownum<=15) where RNUM>=1
